var x = new Array();
var y = new Array();
var z = new Array();
var markersCoordination = me.getAllMarkersCoordination().markersCoordination;

for (var i = 0; i < markersCoordination.length; i++) {
	x.push(markersCoordination[i][0]);
    y.push(markersCoordination[i][1]);
    z.push(markersCoordination[i][2]);
}

x.sort(function(a,b) { return a - b; });
y.sort(function(a,b) { return a - b; });
z.sort(function(a,b) { return a - b; });

paddingMin = function(i){
	if(i<0){
    	return 1.05;
    }else{
    	return 0.95;
    }
};

paddingMax = function(i){
	if(i<0){
    	return 0.95;
    }else{
    	return 1.05;
    }
};
minScale = function(i,j){
	if(i==0){
    	return -(j*0.05);
    }else{
    	return i*paddingMin(i);
    }
};
maxScale= function(i,j){
	if(i==0){
    	return -(j*0.05);
    }else{
    	return i*paddingMax(i) ;
    }
};
isOnePoint = function(e){
	if(e.length==1){
    	return [e[0]-0.05,e[0]+0.05];
    }else if(e.length==0){
    	return [ -0.05,0.05];
    }else{
    	return [minScale(e[0],e[1]),maxScale(e[e.length-1],e[e.length-2])];
    }
};
var result = {
	x: isOnePoint(x),
    y: isOnePoint(y),
    z: isOnePoint(z)
};

